mapper.update:
UPDATE products 
SET product_name=#{productName}, product_contents=#{productContents}, product_category=#{productCategory}, product_rate=#{productRate}, product_sale=#{productSale}
WHERE product_num=#{productNum}

mapper.select:
SELECT * FROM comment WHERE product_num=#{product.productNum}
ORDER BY board_num DESC
LIMIT #{pager.startNum}, #{pager.perPage}
